import "./Document.css";

import img from "../../images/document-icon-18.png";


const Document = ({titre,telecharg}) => {
    return (
        <>
            <div className="card">
                <img
                    src={img}
                    alt="document"
                    onDoubleClick={(event) => {
                        console.log("Double-clic détecté sur l'image !");
                        telecharg(event);
                    }}
                    draggable="false"
                />
                <p className="doc">{titre}</p>
                <div
                    onDoubleClick={() =>
                        console.log("Double-clic détecté sur la div !")
                    }
                    style={{
                        width: "100px",
                        height: "100px",
                        backgroundColor: "lightblue",
                    }}
                >
                    Double-cliquez ici
                </div>
            </div>
        </>
    );
}

export default Document;
