// import React, { useState } from "react";
import "./TrashModal.css";

const TrashModal = ({ show, handleClose, data }) => {
    
    const showHideClassName = show
        ? "modal display-flex"
        : "modal display-none";
    const overlay = show ? "overlay display-block" : "overlay display-none";

    const handleDelete =  (event) => {
        console.log(data, event);
    }
    
  
    return (
        <>
            <div className={overlay}></div>
            <div className={showHideClassName}>
                <section className="modal-trash ">
                  {data[0]==="doc" &&
                    <p class="text">Etes-vous sur de vouloir supprimer la partition "{data[4]}" de votre bibliothèque?</p>
                  } 
                  {data[0]==="user" &&
                    <p class="text">Etes-vous sur de vouloir supprimer l'utilisateur "{data[2]}" de votre bibliothèque?</p>
                  } 
                  {data[0]==="userDoc" &&
                    <p class="text">Etes-vous sur de vouloir supprimer la partition "{data[4]}" pour l'utilisateur "{data[2]}"?</p>
                  } 
                 

                    <div className="buttons">
                        <button
                            onClick={handleDelete}
                            className="btnModal btnModal1"
                        >
                            Supprimer l'élément
                        </button>

                        <button
                            onClick={handleClose}
                            className="btnModal btnModal2"
                        >
                            Fermer
                        </button>
                    </div>
                </section>
            </div>
        </>
    );
};

export default TrashModal;
