import React, { useState, useEffect } from "react";
import "./CreateUser.css";

const CreateUser = ({ show, handleClose }) => {
    const showHideClassName = show
        ? "modal display-flex"
        : "modal display-none";
    const overlay = show ? "overlay display-block" : "overlay display-none";

    const[mail,setMail]= useState("");
    const[mdp,setMdp]= useState("");
    const[alias,setAlias]= useState("");
  
    const[mess, setMess] = useState("");
    use
    useEffect(() => {
      if (mail === "" || mdp === "" || alias === "") {
          setMess("Les 3 champs sont requis pour la création d'un utilisateur!");
      } else {
          setMess("");
      }
    }, [mail,mdp,alias])
    
   

        const handleCreate = () => {
            //  if (mail === "" || mdp === "" || alias === "") {
            //      setMess("Les 3 champs doivent être renseignés!");
            //  } else {
            //      setMess("");
                 console.log(mail, mdp, alias);
            //  }
        };
    

   const handleChangeMail= (event) =>  {
       setMail(event.target.value);
   }
   const handleChangeMdp= (event) =>  {
       setMdp(event.target.value);
   }
   const handleChangeAlias= (event) =>  {
       setAlias(event.target.value);
   }
   
   

  

    return (
        <>
            <div className={overlay}></div>
            <div className={showHideClassName}>
                <section className="modal-main modalCreate">
                    <label htmlFor="mail">Email de connexion*</label>
                    <input type="email" onChange={handleChangeMail} id="mail" />
                    <label htmlFor="mdp">Mot de passe*</label>
                    <input
                        type="text"
                        onChange={handleChangeMdp}
                        id="mdp"
                    />
                    <label htmlFor="alias">
                        Pseudo de l'utilisateur* (affiché dans la liste)
                    </label>
                    <input
                        type="text"
                        onChange={handleChangeAlias}
                        id="alias"
                    />
                   

                    <div className="buttons">
                        <button
                            onClick={handleCreate}
                            className="btnModal btnModal1"
                        >
                            Créer l'utilisateur
                        </button>

                        <button
                            onClick={handleClose}
                            className="btnModal btnModal2"
                        >
                            Fermer
                        </button>
                    </div>
                        <p className="mess">{mess}</p>
                </section>
            </div>
        </>
    );
};

export default CreateUser;
