import "./AdminPage.css";
// Importation des bibliothèques nécessaires
// import React, { useState, useEffect } from "react";
// import { useNavigate } from "react-router-dom";
import DocumentAdmin from "../Admin/DocumentAdmin";
import { useLocation } from "react-router-dom";

const AdminPage = () => {
      const location = useLocation();
      const { state } = location;
      const username = state.username || "'Utilisateur non identifié'";
      const collection = state.collection || [];
    
    
        

    return (
        <>
            <h1>Bonjour {username}</h1>

            <main>
                <ul className="container admin">
                    <div className="|"><button>Ajouter une partition à la collection</button></div>
                    <h2 className="admin">
                        Partitions
                    </h2>
                    {collection.length === 0 ? (
                        <li>Aucun utilisateur disponible</li>
                    ) : (
                        collection.map((user, index) => (
                            <li key={index}>
                                <DocumentAdmin titre={user} />
                            </li>
                        ))
                    )}
                </ul>
                <div className="containerUser"></div>
            </main>
        </>
    );
};
export default AdminPage;
