import "./UserPage.css";
// Importation des bibliothèques nécessaires
import { useLocation } from "react-router-dom";

const UserPage = () => {
     const location = useLocation();
     const { state } = location;
     const username = state.username || "'Utilisateur non identifié'";
     const collection = state.collection || [];
console.log(state);
    return (
        <>
            <h1>Bonjour {username},</h1>
            <h2>Voici les partitions que nous partageons avec toi!</h2>
            <ul>
                {collection.length === 0 ? (
                    <li>Aucun utilisateur disponible</li>
                ) : (
                    collection.map((user, index) => (
                        <li key={index}>
                            <strong>Nom :</strong> {collection[index]} <br />
                           
                        </li>
                    ))
                )}
            </ul>
        </>
    );
};
export default UserPage;
