import React from "react";
import { Navigate } from "react-router-dom";
import AdminPage from "./Admin/AdminPage";
import UserPage from "./UserPage/UserPage";

const SecurityRoute = ({ role, onLogout }) => {
    const userRole = localStorage.getItem("role");

    if (!userRole || (userRole !== "admin" && userRole !== "user")) {
        // Si l'utilisateur n'est pas authentifié ou n'a pas le bon rôle, redirigez-le vers la page de connexion
        return <Navigate to="/login" />;
    }

    // Sinon, affichez le contenu protégé en fonction du role défini
    role === "admin" ? (
        <AdminPage onLogout={handleLogout} />
    ) : (
        <UserPage onLogout={handleLogout} />
    ); 
};

export default SecurityRoute;
