import "./Document.css";

import img from "../../images/document-icon-18.png";
useEffect(() => {
    const handleGlobalDoubleClick = () => {
        console.log("Double-clic détecté globalement !");
    };

    document.addEventListener("dblclick", handleGlobalDoubleClick);

    return () => {
        document.removeEventListener("dblclick", handleGlobalDoubleClick);
    };
}, []);

const Document = ({titre,telecharg}) => {
    return (
        <>
            <div className="card">
                <img
                    src={img}
                    alt="document"
                  
                    onDoubleClick={(event) => {
                        console.log("Double-clic détecté sur l'image !");
                        telecharg(event);
                    }}
                    draggable="false"
                />
                <p className="doc">{titre}</p>
            </div>
        </>
    );
}

export default Document;
