import "./UserCard.css";
import {useRef, useEffect} from "react";
import img_portrait from "../../images/image-portrait-solid.svg";

const UserCard = ({ alias, id, titres, onDragStartUserDocFnct }) => {
    // Créez une référence pour le div
    const userCardRef = useRef(null);

    // Utilisez useEffect pour accéder au texte du paragraphe après le rendu
    useEffect(() => {
        if (userCardRef.current) {
            const paragraphText =
                userCardRef.current.querySelector("p").textContent;
            console.log("Texte du paragraphe:", paragraphText);
        }
    }, []);
    return (
        <>
            <div className="userCard" id={id} draggable="true" ref={userCardRef}>
                <img src={img_portrait} alt="UserCard" draggable="false" />
                <p>{alias}</p>
            </div>
            <div className="popup" id={"popup" + id}>
                <ul>
                    {titres.map((titre) => {
                        return (
                            <li
                                draggable="true"
                                onDragStart={(event) =>
                                    onDragStartUserDocFnct(titre, id, event)
                                }
                                key={alias + titre}
                            >
                                {titre}
                            </li>
                        );
                    })}
                </ul>
            </div>
        </>
    );
};

export default UserCard;
