import React, { useState } from "react";
import "./CreateUser.css";

const CreateUser = ({ show, handleClose }) => {
    const showHideClassName = show
        ? "modal display-flex"
        : "modal display-none";
    const overlay = show ? "overlay display-block" : "overlay display-none";

    const[mail,setMail]= useState("");
    const[alias,setAlias]= useState("");
    const[nom,setNom]= useState("");
    const[prenom,setPrenom]= useState("");



   const handleCreate = ()=>  {
        console.log(mail, alias, nom, prenom);
   }

   const handleChangeMail= (event) =>  {
       setMail(event.target.value);
   }
   const handleChangeAlias= (event) =>  {
       setMail(event.target.value);
   }
   const handleChangeNom= (event) =>  {
       setMail(event.target.value);
   }
   const handleChangePrenom= (event) =>  {
       setMail(event.target.value);
   }
   

  

    return (
        <>
            <div className={overlay}></div>
            <div className={showHideClassName}>
                <section className="modal-main">
                    <input type="email" onChange={handleChangeMail} />
                    <input type="text" onChange={handleChangeAlias} />
                    <input type="text" onChange={handleChangeMail} />
                    <input type="text" onChange={handleChangeMail} />
                        <button
                            onClick={handleCreate}
                            className="btnModal btnModal1"
                        >
                        Cr√©er l'utilisateur
                        </button>
                   
                    <button
                        onClick={handleClose}
                        className="btnModal btnModal2"
                    >
                        Fermer
                    </button>
                </section>
            </div>
        </>
    );
};

export default CreateUser;
