import React, { useState } from "react";
import  "./Modal.css";

const FileUpload = ({show, handleClose}) => {

     const showHideClassName = show
         ? "modal display-flex"
         : "modal display-none";

    const [selectedFile, setSelectedFile] = useState(null);

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            alert("Un fichier doit être sélectionné!");
            return;
        }

        const formData = new FormData();
        formData.append("file", selectedFile);

        try {
            const response = await fetch("/upload", {
                method: "POST",
                body: formData,
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data);
                alert("Upload réussi");
            } else {
                alert("Échec de l'upload");
            }
        } catch (error) {
            console.error("Erreur:", error);
            alert("Échec de l'upload");
        }
    };

    return (
        <div className={showHideClassName}>
            <section className="modal-main">
                <div className="upLoad">
                    <input type="file" onChange={handleFileChange} className="inputUpload"/>
                    <button onClick={handleUpload} className="btnModal btnModal1">
                        Télécharger
                    </button>
                </div>
                <button onClick={handleClose} className="btnModal btnModal2">
                    Fermer
                </button>
            </section>
        </div>
    );
};

export default FileUpload;
