// import React, { useState } from "react";
import "./TrashModal.css";

const TrashModal = ({ show, handleClose, data }) => {
    
    const showHideClassName = show
        ? "modal display-flex"
        : "modal display-none";
    const overlay = show ? "overlay display-block" : "overlay display-none";

     const handleDelete = async (event) => {
         console.log(data, event);

         // Appel à l'API pour supprimer l'élément
         try {
             const response = await fetch("votre-url-api", {
                 method: "DELETE",
                 // Ajoutez les options nécessaires à votre requête
             });

             if (response.ok) {
                 console.log("Élément supprimé avec succès");
                 // Fermer la popup après une suppression réussie
                 handleClose();
             } else {
                 console.error("Erreur lors de la suppression");
             }
         } catch (error) {
             console.error("Erreur lors de la requête", error);
         }
     };
    
  
    return (
        <>
            <div className={overlay}></div>
            <div className={showHideClassName}>
                <section className="modal-trash ">
                  {data[0]==="doc" &&
                    <p className="text">Etes-vous sur de vouloir supprimer la partition "{data[4]}" de votre bibliothèque?</p>
                  } 
                  {data[0]==="user" &&
                    <p className="text">Etes-vous sur de vouloir supprimer l'utilisateur "{data[2]}" de votre bibliothèque?</p>
                  } 
                  {data[0]==="userDoc" &&
                    <p className="text">Etes-vous sur de vouloir supprimer la partition "{data[4]}" pour l'utilisateur "{data[2]}"?</p>
                  } 
                 

                    <div className="buttons">
                        <button
                            onClick={handleDelete}
                            className="btnModal btnModal1"
                        >
                            Supprimer l'élément
                        </button>

                        <button
                            onClick={handleClose}
                            className="btnModal btnModal2"
                        >
                            Fermer
                        </button>
                    </div>
                </section>
            </div>
        </>
    );
};

export default TrashModal;
